def getPixel( x,y ):
   #autogenerated by mappy's painter      BOARD_SIZE = 8;   cK = [0,0,0];   cg = [128,128,128];   cW = [255,255,255];   ca = [128,0,0];   cR = [255,0,0];   ch = [255,128,128];   cb = [0,128,0];   cG = [0,255,0];   ci = [128,255,128];   cc = [0,0,128];   cB = [0,0,255];   cj = [128,128,255];   cd = [128,128,0];   cY = [255,255,0];   ck = [255,255,128];   ce = [128,0,128];   cP = [255,0,255];   cl = [255,128,255];   cf = [0,128,128];   cZ = [0,255,255];   cm = [128,255,255];   board = [       [cB, cB, cB, cB, cB, cB, cB, cB],       [cB, cB, cB, cG, cG, cB, cB, cB],       [cB, cB, cG, cG, cG, cG, cB, cB],       [cB, cG, cG, cG, cG, cG, cG, cB],       [cR, cG, cG, cG, cG, cG, cG, cR],       [cR, cR, cG, cY, cY, cG, cR, cR],       [cR, cR, cR, cY, cY, cR, cR, cR],       [cR, cR, cR, cR, cR, cR, cR, cR]   ];   r,g,b = board[int(y*BOARD_SIZE)][int(x*BOARD_SIZE)]   
   return r%256,g%256,b%256